package org.db.psd.model;

/**
 * 与员工类型表（employeeJob）一一对应的实体类
 * @author Administrator
 *
 */
public class EmployeeJob {

	private int employeeJobId;

	private String employeeJobName;//超级管理员：1，采购员：2，仓库员：3，销售员：4
	private String employeeDescribe;

	public EmployeeJob() {
		// TODO Auto-generated constructor stub
	}

	public EmployeeJob(int employeeJobId, String employeeJobName, String employeeDescribe) {
		super();
		this.employeeJobId = employeeJobId;
		this.employeeJobName = employeeJobName;
		this.employeeDescribe = employeeDescribe;
	}

	public int getEmployeeJobId() {
		return employeeJobId;
	}

	public void setEmployeeJobId(int employeeJobId) {
		this.employeeJobId = employeeJobId;
	}

	public String getEmployeeJobName() {
		return employeeJobName;
	}

	public void setEmployeeJobName(String employeeJobName) {
		this.employeeJobName = employeeJobName;
	}

	public String getEmployeeDescribe() {
		return employeeDescribe;
	}

	public void setEmployeeDescribe(String employeeDescribe) {
		this.employeeDescribe = employeeDescribe;
	}

}
